import { useDispatch, useSelector } from "react-redux";
import {toast} from "react-hot-toast"
import { add , remove } from "../Redux/Slices/cartSlice";
const Product = ({post}) => {

  const cart = useSelector((state)=>state.cart);

  const dispatch = useDispatch();

  function addToCart(){
    dispatch(add(post));
    toast.success("Item Added to Cart")
  }
  
  function removeFromCart(){
    dispatch(remove(post.id));
    toast.success("Item Removed from Cart")
  }
  return (
    <div className="flex flex-col items-center justify-between hover:scale-110 transition duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl hover:shadow-[0_2.8px_2.2px_rgba(0,_0,_0,_0.034),_0_6.7px_5.3px_rgba(0,_0,_0,_0.048),_0_12.5px_10px_rgba(0,_0,_0,_0.06),_0_22.3px_17.9px_rgba(0,_0,_0,_0.072),_0_41.8px_33.4px_rgba(0,_0,_0,_0.086),_0_100px_80px_rgba(0,_0,_0,_0.12)]">
      <div>
        <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">{post.title}</p>
      </div>
      <div>
        <p className="w-40 text-gray-400 font-normal text-[10px] text-left " >{post.description.split(" ").slice(0,10).join(" ")+ "..." }</p>
      </div>
      <div c>
        <img src= {post.image} alt="" />
      </div>
      <div>
        <p>{post.price}</p>
      </div>
        {
          cart.some( (p) => p.id === post.id) ?
           (
            <button onClick={removeFromCart}>
              Remove Item
            </button>
           ):
           (
            <button onClick={addToCart}>
              Add to Cart
            </button>
           )
        }
    </div>
  );
};

export default Product;
